-- Substitute your own instance name and net names.

your_instance_name : FFT
        port map (
            clk => clk,
            ce => ce,
            reset => reset,
            start => start,
            fwd_inv => fwd_inv,
            mrd => mrd,
            mwr => mwr,
            xn_re => xn_re,
            xn_im => xn_im,
            ovflo => ovflo,
            done => done,
            edone => edone,
            busy => busy,
            xk_re => xk_re,
            xk_im => xk_im);
